INSERT INTO `productos` VALUES (20, 'Líquidos', 'eco_pol_1000ml.jpg', 'eco_pol_1000ml_mini.jpg', '', '', 'Agente reductor de tensiones superficiales', 'ECOPOL', '<table width="400" border="0" align="center" cellpadding="0" cellspacing="0">\r\n      <tr>\r\n        <td><h3>Agente reductor de tensiones superficiales para todos los revestimientos de fosfato y escayola.</h3>\r\n    <p>Para la t&eacute;cnica de colados. ECOPOL crea un puente molecular entre la cera y el revestimiento produciendo as&iacute; una mejor fluidez y adaptaci&oacute;n del revestimiento a la cera.</p>\r\n    <p>ECOPOL produce colados lisos y libres de burbujas. Apropiado tambi&eacute;n para vaciados de impresiones de silicona.</p>\r\n    <p>ECOPOL no contiene productos disolventes de cera. No influye al fraguado de revestimiento ni a la dureza superficial de revestimientos de escayolas.    </p>\r\n    <p>1000 ml con botella pulverizador. </p>\r\n    <table width="98%" border="0" cellspacing="0" cellpadding="0">\r\n      <tr>\r\n        <td><div align="right">Ref.:121 00 15 </div></td>\r\n      </tr>\r\n    </table>\r\n    </td>\r\n      </tr>\r\n    </table>');
INSERT INTO `productos` VALUES (21, 'Líquidos', 'eco_por_1000ml.jpg', 'eco_por_1000ml_mini.jpg', '', '', 'Agente separador de escayola contra escayola.', 'ECOPOR', '<table width="400" border="0" align="center" cellpadding="0" cellspacing="0">\r\n      <tr>\r\n        <td><h3>Agente separador de escayola contra escayola.</h3>\r\n    <p>Efecto de separaci&oacute;n garantizado de varias semanas. No forma pel&iacute;cula alguna.</p>\r\n    <p>Separaci&oacute;n f&aacute;cil y exacta de escayola con escayola.</p>\r\n    <p>ECOPOR sella los poros de la escayola contra agua y suciedad y crea una superficie lisa y repelente al agua.</p>\r\n    <p>Los modelos no cambian su color.</p>\r\n    <p>Campo de aplicaci&oacute;n: Split Cast, llaves, arcadas, modelos, muflas, etc.</p>\r\n    <p>1000 ml con botella pulverizador.</p>\r\n    <table width="98%" border="0" cellspacing="0" cellpadding="0">\r\n      <tr>\r\n        <td><div align="right">Ref.:121 00 16 </div></td>\r\n      </tr>\r\n    </table>\r\n    </td>\r\n      </tr>\r\n    </table>');
